class NavTree{constructor(){this.init()}init(){this.buildBreadcrumbs(),this.buildSectionNav()}buildBreadcrumbs(){const e=document.getElementById("breadcrumbs");if(!e)return;const t=window.location.pathname.split("/").filter(Boolean);let a='<a href="/">Home</a>',n="";t.forEach(((e,r)=>{n+="/"+e;const i=r===t.length-1;a+=" > ",a+=i?'<span class="current">'+this.formatName(e)+"</span>":'<a href="'+n+'">'+this.formatName(e)+"</a>"})),e.innerHTML=a}buildSectionNav(){document.getElementById("section-nav")}formatName(e){return e.split("-").map((e=>e.charAt(0).toUpperCase()+e.slice(1))).join(" ")}}new NavTree;